import React from "react";
import styles from "./box1.module.css";

const Box2 = (props) => {
    console.log(props.result);
    const boxStyle = {
        border:
            props.result === "짐"
                ? "20px solid red"
                : props.result === "이김"
                ? "20px solid green"
                : "",
    };
    return (
        <div className={styles.box} style={boxStyle}>
            <h1>{props.title}</h1>
            {props.item && props.item.img ? (
                <img className={styles.img} src={props.item.img} alt="" />
            ) : (
                <img
                    className={styles.img}
                    src="rockscissorspaper/src/img/가위바위보이미지/nothing.jpeg"
                    alt="기본 이미지"
                />
            )}
            <h2>{props.result}</h2>
        </div>
    );
};
export default Box2;
